import {createContext, useState} from 'react';
import App from '../App';
import articles from '../Utiles/articles';

export const contextsStore = createContext({
    articlesList:[],
    setArticlesList :()=>{},
    currentArticle :{},
    setCurrentArticle: ()=>{},
    inArticlePage: null,
    setInArticlePage:()=>{}
});

const ContextsProvider =()=>{
    const [articlesList,setArticlesList] = useState(articles());
    const [currentArticle,setCurrentArticle] = useState(articlesList[0]);
    const [inArticlePage,setInArticlePage] = useState(false);
    return(
        <contextsStore.Provider
        value={{
            articlesList,
            setArticlesList,
            currentArticle,
            setCurrentArticle,
            inArticlePage,
            
        }}
        >
            <App/>
        </contextsStore.Provider>
    )
};
export default ContextsProvider;
