import React, { useContext } from "react";
import {useParams} from 'react-router-dom';
import Article from "../../components/custom/Article";
import { contextsStore } from "../../Contexts";

const SingleArticlePage = ()=>{
    const params = useParams();
    const {articlesList,currentArticle,setCurrentArticle,setInArticlePage} = useContext(contextsStore);
    const filterd = articlesList.filter((item)=> item.id == params.SingleArticlePage );
    const current = filterd[0];
    setInArticlePage(true);
    console.log(current,'currentPage');
    return(
        <Article article={current}/>

    )
};
export default SingleArticlePage;
