import { useEffect } from 'react';
import {createContext, useState} from 'react';
import App from '../App';
import articles from '../Utiles/articles';
import axios from 'axios';
import { useCookies } from 'react-cookie';
export const contextsStore = createContext({
    articlesList:[],
    setArticlesList :()=>{},
    currentArticle :{},
    setCurrentArticle: ()=>{},
    inArticlePage: null,
    setInArticlePage:()=>{},
    isDelete:null,
    setIsDelete:()=>{},
    isLogedIn: false,
    setIsLogedIn:()=>{},
    cookie:'',

    
});

const ContextsProvider =()=>{
    const [articlesList,setArticlesList] = useState([]);
    const [isDelete,setIsDelete] = useState(false);
    const [cookies,SetCookie,removeCookie] = useCookies();
    const [isLogedIn,setIsLogedIn]=useState(false);
    useEffect(() => {
        axios.get(`https://62de61cc9c47ff309e71cc65.mockapi.io/api/articlestack/article`)
            .then((response) => {
                setArticlesList(response.data)
                console.log(response.data);
            })
    }, [isDelete]);
    const [currentArticle,setCurrentArticle] = useState(articlesList[0]);
    const [inArticlePage,setInArticlePage] = useState(false);
    return(
        <contextsStore.Provider
        value={{
            articlesList,
            setArticlesList,
            currentArticle,
            setCurrentArticle,
            inArticlePage,
            setInArticlePage,
            isDelete,
            setIsDelete,
            isLogedIn,
            setIsLogedIn
        }}
        >
            <App/>
        </contextsStore.Provider>
    )
};
export default ContextsProvider;
