import { ButtonBase, CardActions, IconButton, makeStyles } from "@material-ui/core";
import React, { useContext } from "react";
import FavoriteIcon from '@material-ui/icons/Favorite';
import {Link} from 'react-router-dom'
import { contextsStore } from "../../../../../Contexts";
const useStyles = makeStyles((theme)=>({
  button:{
    color: theme.palette.secondary.main
  }
}))

const Actions = ({currentId})=>{
  const classes = useStyles();
  const {articlesList,currentArticle,setCurrentArticle} = useContext(contextsStore);
  const handleLikeArtilce = ()=>{
    console.log('handle Like Article');
  }
  const handleClickMore = ()=>{
    console.log(articleList);
    const current = articlesList.filter(item=> item.id == currentId);
    console.log('current rticle',current);
  }
    return(
        <CardActions disableSpacing>
        <IconButton aria-label="add to favorites" onClick={handleLikeArtilce}>
          <FavoriteIcon />
        </IconButton>
        <ButtonBase
        className={classes.button}
        onClick={handleClickMore}
        >
          <Link to={`/signleArticles/${currentId}`} key={currentId}>
                            more
                        </Link>
        </ButtonBase>
      </CardActions>
    )
};

export default Actions;
