import React, { useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Picture from './Components/Picture';
import ArticleHeader from './Components/ArticleHeader';
import Actions from './Components/Actions';
import { Box, useEventCallback } from '@material-ui/core';
import { contextsStore } from '../../../Contexts';

const useStyles = makeStyles((theme)=>({
  card:{
    hight:'50vh',
    width:'70%',
    paddingLeft:'1rem',
    paddingRight:'1rem'
  }

}))
export default function Article({articleId}) {
  const  {articlesList} = useContext(contextsStore);
  const filterd = articlesList.filter((item)=>item.id == articleId);
  const current = filterd[0];
  const classes = useStyles();
  console.log('current: ',current);
 
  return (
    <Card  className={classes.card}>
      <ArticleHeader current={current}/>
     <Box>
     <Picture picture={current.picture}/>
     </Box>
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          chand satr aval maghale inja neshun dade she
        </Typography>
      </CardContent>
      <Actions articleId={current.id}/>
    </Card>
  );
}
