import { CardHeader, IconButton, Menu, MenuItem } from "@material-ui/core";
import React, { useState } from "react";
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Uavatar from "../../../Users/Components/Uavatar";
import ADate from "../ADate";
import Title from "../Title";
import {v4 as uuidv4} from 'uuid';
const ArticleHeader =()=>{
  const [anchorEl, setAnchorEl] = useState('')
  const handleMoreVert =(e)=>{
    setAnchorEl(e.currentTarget);
  };
  const handleOpion =(e)=>{
    console.log(e.target.value);
  }

  const open = Boolean(anchorEl);

  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleEdite = ()=>{
    console.log('Edite');
  };
  const handleDelete = ()=>{
    clg('Delete')
  };
  const handleDetails = ()=>{
    clg('Details')
  };
  return (
        <CardHeader
        avatar={
         <Uavatar />
        }
        action={
          <>
          <IconButton aria-label="more" 
          onClick={handleMoreVert} 
          aria-haspopup="true"
          aria-controls="long-menu"
          >
            <MoreVertIcon />
          </IconButton>
          <Menu 
          
          anchorEl={anchorEl} 
          onClose={handleClose} 
          open={open}>
            <MenuItem onClick={handleEdite}>Edite</MenuItem>
            <MenuItem onClick={handleDelete}>Delete</MenuItem>
            <MenuItem onClick={handleDetails}>Details</MenuItem>
          </Menu>
          </>
        }
        title={
          <Title />
        }
        subheader={
          <ADate />
        }
      />
  )
};
export default ArticleHeader;
