import { Avatar, Box, Button, Checkbox, FormControlLabel, Grid, TextField, Typography } from "@material-ui/core";
import React, { useEffect } from "react";
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { LOGIN } from "../../../api"; 
import {useFormik} from 'formik'
import useStyle from "./SignIn.style";
import axios from 'axios';
import {useCookies} from 'react-cookie';
import * as yup from 'yup';   
//for fetch data use useEffect babe
const SignIn =()=>{
    const classes = useStyle();
    const [cookies,SetCookie,removeCookie] = useCookies();
    let schema = yup.object.shape({
      fullname:yup.string('enter fullname in a valid form pleas').required('fullname is required!'),
      username: yup.string().required('username is required'),
      email:yup.string().email().required('email is required'),
      password:yup.string().required().min(6).max(8),
      confirmpassword:yup.string().required()
    })
    const handleSubmitForm= async (values)=>{
        try{
          const res = await axios.post(LOGIN,{
            fullname : values.fullname,
            username:values.username,
            email:values.email,
            password:values.password,
            confirmpassword:values.confirmpassword,
         });
         const {data} = await res;
         SetCookie('token',data.token,{
          maxAge: 60*60*24*7
         })
        }catch(ex){
          console.log(ex);
        }
    };
     const formik = useFormik({
        initialValues:{
            fullname : '',
            username:'',
            email:'',
            password:'',
            confirmpassword:'',
      },
      onSubmit: handleSubmitForm,
      validationSchema:{}
     },
    
     );

    return (
        <Box
        className={classes.root}
      >
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <Box component='form' onSubmit={formik.handleSubmit }  sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="fullName"
            label="Full Name"
            name="fullname"
            autoComplete="input"
            autoFocus
            value={formik.values.fullname}
            onChange={formik.handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            id="username"
            label="UserName"
            name="username"
            autoComplete="input"
            autoFocus
            value={formik.values.username}
            onChange={formik.handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
            value={formik.values.email}
            onChange={formik.handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            value={formik.values.password}
            onChange={formik.handleChange}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="confirmpassword"
            label="Password"
            type="password"
            id="confirmassword"
            autoComplete="current-password"
            value={formik.values.confirmpassword}
            onChange={formik.handleChange}
          
          />
          <FormControlLabel
            control={<Checkbox value="remember" color="primary" />}
            label="Remember me"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Sign In
          </Button>
        </Box>
      </Box> 
    )
};

export default SignIn;
