import Layout from "../components/common/Layout";
import Article from "../components/custom/Article";
import articles from "../Utiles/articles"
import { useState } from "react";
import { Box, Typography } from '@material-ui/core';
import {Routes, Route} from 'react-router-dom'
import Login from "../Pages/Auth/SignIn";
import SignIn from "../Pages/Auth/SignIn";
const App =()=>{
    const [allArticles, setAllArticles] = useState(articles());
    return (
       <Layout sx={{backgroundColor:'red'}}>
        <Routes>
            <Route path='/' element={
                 <Box
                 component='ul'>
                 {allArticles.map((article) =>(
                     <Box key={article.id} component='li' my={4}>
                         <Article  id={article.id} 
                         name={article.name}
                         author={article.author}
                         subject={article.subject}
                         picture={article.picture}
                         summery={article.summery}
                         context={article.context}
                         date={article.dateModified}
                         likes={article.likes}
                         comments={article.comments}
                          />
                     </Box>
                 ))}
                 </Box>
            }/>
            <Route path='/Articls' element={
                <Typography sx={{marginTop:'4rem'}}>your articles</Typography>
            } />
            <Route path='/Add' element={
                <Box my={8} mx={8}>
                    <Typography>add new article</Typography>
                </Box>
                
            } />
            <Route path='/Drafts' element={
                <Typography>Drafts</Typography>
            } />
            <Route path='/Trash' element={
                <Typography>Trash  </Typography>
            } />
            <Route path='/SignIn' element={
                <SignIn/>
            } />
            <Route path='/profile' element={
                <Typography>Login/logout  </Typography>
            } />
        </Routes>
       </Layout>
    )
};

export default App
