import React, { useContext } from "react";
import {useFormik} from 'formik'
import useStyle from "../crud.style";
import * as yup from 'yup';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import {v4 as uuidv4} from 'uuid';
import {Button,TextField,Box,Avatar,Typography,FormControlLabel,Checkbox} from '@material-ui/core';
import { contextsStore } from "../../../Contexts";
const CreateArticle = ()=>{
        const classes = useStyle();
        const {articlesList,setArticlesList} = useContext(contextsStore)
        const navigate = useNavigate();
        const schema = yup.object().shape({
            name:yup.string().required(),
          subject: yup.string().required('subject is required'),
          summery:yup.string().required(),
          context:yup.string().required(),
        //   picture:yup.mixed().required('A file is required')
        });
        // let id = uuidv4();
        // let date = new Date();
        // let likes = 0;
        // let authorId = '1';
        
        const handleSubmitForm= async(e)=>{
            console.log('dfdfdf');
        //     try {
        //         await axios.post(`https://62de61cc9c47ff309e71cc65.mockapi.io/api/articlestack/article`, {
        //             subject ,
        //             summery ,
        //             context,
        //             date,
        //             likes,
        //             authorId,
        //             // picture,
        //         }).then(res=> console.log({res}))
        //     } catch (error) {
        //         console.log({error});
            }
        
        //      /*navigate("/signleArticles/:SingleArticlePage")*/
        // };
        // const test =() => {
        //     console.log('dfdsfd');
        // }
         const formik = useFormik({
            initialValues:{
                name:'',
                subject: '',
                summery:'',
                context:'',
                // picture:''
          },
          onSubmit: handleSubmitForm,
          validationSchema:schema
         },
        
         );
        return (
            <Box
            className={classes.root}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              R
            </Avatar>
            <Typography component="h1" variant="h5">
              Write Article
            </Typography>
            <form onSubmit={formik.handleSubmit }>

            <Box sx={{ mt: 1 }}>
        
            <TextField
                margin="normal"
                required
                fullWidth
                id="name"
                label="name"
                name="name"
                autoComplete="input"
                autoFocus
                value={formik.values.name}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                helperText={formik.errors.name}
                error={formik.errors.name?true:false}
                />  
              <TextField
                margin="normal"
                required
                fullWidth
                id="subject"
                label="Subject"
                name="subject"
                autoComplete="input"
                autoFocus
                value={formik.values.subject}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                helperText={formik.errors.subject}
                error={formik.errors.subject?true:false}
                />  
              <TextField
                margin="normal"
                required
                fullWidth
                id="summery"
                label="Summery"
                name="summery"
                autoComplete="input"
                autoFocus
                value={formik.values.summery}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                helperText={formik.errors.summery}
                error={formik.errors.summery?true:false}
                />
              <TextField
                margin="normal"
                required
                fullWidth
                id="context"
                label="Article contect"
                name="context" 
                autoComplete="input"
                autoFocus
                multiline
                minRows={5}
                value={formik.values.context}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                helperText={formik.errors.context}
                error={formik.errors.context?true:false}
                />
             <Box>
             <input
                    accept="image/*"
                    className={classes.input}
                    id="contained-button-file"
                    name="picture"
                    type="file"
                    />
                <label htmlFor="contained-button-file">
                    <Button variant="contained" component="span">
                    Upload
                    </Button>
                </label>
             </Box>
              <FormControlLabel
                control={<Checkbox value="remember" color="primary" />}
                label="Remember me"
                />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
                // onClick={test}
                >
                Create
              </Button>
            </Box>
                  </form>
          </Box> 
        )
    };
export default CreateArticle;
