import { Box, Divider, Drawer, List, ListItem, ListItemIcon, ListItemText, makeStyles } from "@material-ui/core";
import React from "react";
import Uavatar from "../../custom/Users/Components/Uavatar";
import UserName from "../../custom/Users/Components/UserName";
const drawerWidth = 240;
const useStyles = makeStyles((theme)=>({
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    

}));
const Sidebar = ()=>{
    const classes = useStyles();
    const goToProfile = ()=>{
        console.log('go to profile');
      }
    return (
        <Drawer
            variant="permanent"
            anchor="left"
          >
            <Box className={classes.toolbar}/>
            <Divider/>
            <Box 
            display='inline-flex'
            my='1rem'
            sx={{
              
              flexDirection:'row',
              justifyContent: 'space-around',
              alignItems:'center'
            }}
            onClick={goToProfile}
            >
              <Uavatar/>
              <UserName />
            </Box>
            <Divider />
            <List>
              {['All Articles','Add', 'Drafts', 'Trash','logout'].map((text, index) => (
                <ListItem button key={text}>
                  <ListItemIcon>{index % 2 === 0 ? <ListItemIcon /> : <ListItemIcon />}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List>
          </Drawer>
    )
}
export default Sidebar;
