import React from "react";
import Layout from "../components/common/Layout";
import Article from "../components/custom/Article";
import { useContext} from "react";
import { Box, Typography } from '@material-ui/core';
import {Routes, Route} from 'react-router-dom'
import SignIn from "../Pages/Auth/SignIn";
import {Link} from 'react-router-dom'
import SingleArticlePage from "../Pages/SignleArticlePage";
import { contextsStore } from "../Contexts";
import Home from "../Pages/Home";
const App =()=>{
    const {articlesList,setCurrentArticle,currentArticle} = useContext(contextsStore);
    return (
       <Layout sx={{backgroundColor:'red'}}>
        <Routes>
            <Route path='/' element={
               <Home/>
            }/>
            <Route path='/Articles' element={
                <Typography sx={{marginTop:'4rem'}}>your articles</Typography>
            } />
            <Route path='/Add' element={
                <Box my={8} mx={8}>
                    <Typography>add new article</Typography>
                </Box>
                
            } />
            <Route path='/Drafts' element={
                <Typography>Drafts</Typography>
            } />
            <Route path='/Trash' element={
                <Typography>Trash  </Typography>
            } />
            <Route path='/SignIn' element={
                <SignIn/>
            } />
            <Route path='/profile' element={
                <Typography>Login/logout  </Typography>
            } />
            <Route path='/signleArticles/:SingleArticlePage' element={
                <SingleArticlePage/>
            } />
        </Routes>
       </Layout>
    )
};

export default App
