import { Box, Divider, Drawer, List, ListItem, ListItemIcon, ListItemText, makeStyles } from "@material-ui/core";
import React from "react";
import Uavatar from "../../custom/Users/Components/Uavatar";
import UserName from "../../custom/Users/Components/UserName";
import AddRoundedIcon from '@material-ui/icons/AddRounded';
import {NavLink } from 'react-router-dom'
const drawerWidth = 240;
const useStyles = makeStyles((theme)=>({
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
        marginTop: theme.Appbar.hight
    },
    drawerPaper: {
        width: drawerWidth,
    },
    

}));
const Sidebar = ()=>{
    const classes = useStyles();
    const goToProfile = ()=>{
        console.log('go to profile');
      }
    return (
        <Drawer
            variant="permanent"
            anchor="left"
          >
            <Box className={classes.drawer}
            variant="permanent"
            />
            <Divider/>
            <Box 
            display='inline-flex'
            my='1rem'
            sx={{
              
              flexDirection:'row',
              justifyContent: 'space-around',
              alignItems:'center'
            }}
            onClick={goToProfile}
            >
              <Uavatar/>
              <UserName />
            </Box>
            <Divider />
            <List>
              {['Articles','Add', 'Drafts', 'Trash','logout'].map((text, index) => (
                <NavLink>
                  <ListItem button key={text} >
                    <ListItemIcon>{index % 2 === 0 ? <AddRoundedIcon /> : <AddRoundedIcon />}</ListItemIcon>
                    <ListItemText primary={text} />
                  </ListItem>
                </NavLink>
                
              ))}
            </List>
          </Drawer>
    )
}
export default Sidebar;
