import React, { useContext, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Picture from './Components/Picture';
import ArticleHeader from './Components/ArticleHeader';
import Actions from './Components/Actions';
import {Box} from '@material-ui/core';
import { contextsStore } from '../../../Contexts';
import Summery from './Components/Summery';
import { Flag } from '@material-ui/icons';

const useStyles = makeStyles((theme)=>({
  card:{
    hight:'50vh',
    width:'70%',
    paddingLeft:'1rem',
    paddingRight:'1rem'
  }

}))
export default function Article({article}) {
  const  {articlesList,setInArticlePage} = useContext(contextsStore);
  const filterd = articlesList.filter((item)=>item.id == articleId);
  const current = filterd[0];
  setInArticlePage(false);
  const classes = useStyles();
  
  return (
    <Card  className={classes.card}>
      <ArticleHeader current={current}/>
     <Box>
     <Picture picture={current.picture}/>
     </Box>
      <CardContent>
        <Summery summery={current.summery}/>
      </CardContent>
      <Actions current={current}/>
      <Box>

      </Box>
    </Card>
  );
}
