import { Box, Divider, Drawer, List, ListItem, ListItemIcon, ListItemText, makeStyles } from "@material-ui/core";
import React from "react";
import Uavatar from "../../custom/Users/Components/Uavatar";
import UserName from "../../custom/Users/Components/UserName";
import AddRoundedIcon from '@material-ui/icons/AddRounded';
import {NavLink } from 'react-router-dom'
const drawerWidth = 240;
const useStyles = makeStyles((theme)=>({
    root:{
      backgroundColor:theme.palette.secondary.main,
      marginLeft:'1'
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
        marginTop: theme.Appbar.hight
    },
    drawerPaper: {
        width: '100%',
    },
    

}));
const Sidebar = ()=>{
    const classes = useStyles();
    const goToProfile = ()=>{
        console.log('go to profile');
      }
    return (
        <Box
            className={classes.root}
            variant="permanent"
            anchor="left"
            marginRight='0'
          
          >
            <Box className={classes.drawer}
            variant="permanent"
            />
            <Divider/>
           <NavLink to='/profile'>
           <Box 
            display='inline-flex'
            my='1rem'
            sx={{
              
              flexDirection:'row',
              justifyContent: 'space-around',
              alignItems:'center'
            }}
            onClick={goToProfile}
            >
              <Uavatar/>
              <UserName />
            </Box>
           </NavLink>
            <Divider />
            <List>
              {['Articles','Add', 'Drafts', 'Trash','SignIn'].map((text, index) => (
                <NavLink to={`/${text}`} className={(active)=>(active.isActive? 'active':'notActive')} key={text}>
                  <ListItem button >
                    <ListItemIcon>{index % 2 === 0 ? <AddRoundedIcon /> : <AddRoundedIcon />}</ListItemIcon>
                    <ListItemText primary={text} />
                  </ListItem>
                </NavLink>
                
              ))}
            </List>
          </Box>
    )
}
export default React.memo(Sidebar);
