import { useEffect } from 'react';
import {createContext, useState} from 'react';
import axios from 'axios';
import { useCookies } from 'react-cookie';
import Cookies from "js-cookie";
export const contextsStore = createContext({
    articlesList:[],
    setArticlesList :()=>{},
    currentArticle :{},
    setCurrentArticle: ()=>{},
    inArticlePage: null,
    setInArticlePage:()=>{},
    isDelete:null,
    setIsDelete:()=>{},
    isLogedIn: false,
    setIsLogedIn:()=>{},
    cookie:'',
    setCookie:()=>{},
    removeCookie:()=>{},
    userStatus:'',
    setUserStatus:()=>{},
    currentUser:{},
    setCurrentUser:()=>{},
    currentCooki :{},
    setCurrentC
    
});

const ContextsProvider =({children})=>{
    const [articlesList,setArticlesList] = useState([]);
    const [isDelete,setIsDelete] = useState(false);
    const [cookie,setCookie,removeCookie] = useCookies();
    const [isLogedIn,setIsLogedIn]=useState(cookie.token);
    const[userStatus,setUserStatus] = useState('usual');
    const [currentUser,setCurrentUser] = useState({});

    useEffect(() => {
        axios.get(`https://62de61cc9c47ff309e71cc65.mockapi.io/api/articlestack/article`)
            .then((response) => {
                setArticlesList(response.data)
                console.log(response.data);
            })
    }, [isDelete]);
    const [currentArticle,setCurrentArticle] = useState(articlesList[0]);
    const [inArticlePage,setInArticlePage] = useState(false);
    return(
        <contextsStore.Provider
        value={{
            articlesList,
            setArticlesList,
            currentArticle,
            setCurrentArticle,
            inArticlePage,
            setInArticlePage,
            isDelete,
            setIsDelete,
            cookie,
            setCookie,
            removeCookie,
            isLogedIn,
            setIsLogedIn,
            userStatus,
            setUserStatus,
            currentUser,
            setCurrentUser,
        }}
        >
        {children} 
        </contextsStore.Provider>
    )
};
export default ContextsProvider;
