import { AppBar, Box, Divider, Drawer, Grid, List, ListItem, ListItemIcon, ListItemText, makeStyles, Toolbar} from "@material-ui/core";
import React from "react";
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import Header from "../Header";
import Article from "../../custom/Article";
import Uavatar from "../../custom/Users/Components/Uavatar";
import UserName from "../../custom/Users/Components/UserName";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection:'column'
  },
  appBar: {
    width:'100%',
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
  },
  articles:{
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    justifyContent:'center',
    alignItems:'center',
  },
  posts:{
      marginLeft: '25%'
  }
}));

const Main=()=>{
    const classes = useStyles();
    return (
      <Box >
        <AppBar
        position="fixed"
        className={classes.appBar}
        >
          <Toolbar >
            <Header/>
          </Toolbar>
        </AppBar>
        <Box>
          <Drawer
            variant="permanent"
            anchor="left"
          >
            <Box className={classes.toolbar}/>
            <Divider/>
            <Box 
            display='inline-flex'
            sx={{
              flexDirection:'row',
              justifyContent: 'space-between'

            }}
            >
              <Uavatar/>
              <UserName/>
            </Box>
            <Divider />
            <List>
              {['All mail', 'Trash', 'Spam'].map((text, index) => (
                <ListItem button key={text}>
                  <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List>
          </Drawer>
          <Box
          width='100%'
          className={classes.articles}
          display='flex'
          sx={{justifyContent:'center', alignItems:'center', flexDirection:'column'}}
          
          >
            <Box className={classes.toolbar}/>
            <Grid 
            container
            direction="column"
            className={classes.posts}
            spacing={6}
            >
              <Grid item>
              <Article/>
              </Grid>
              <Grid item>
              <Article/>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Box>
    )
};

export default Main;
